// In Returns a slice of arguments from an argument list
func In(params ...*ast.CXArgument) []*ast.CXArgument {
	return params
}

// Out Returns a slice of arguments from an argument list
func Out(params ...*ast.CXArgument) []*ast.CXArgument {
	return params
}

